jQuery(document).ready(function(t){"use strict";var e=t(document).find(".yith-waitlist-send-mail");e.on("click",function(e){e.preventDefault();var n=t(this),a=n.data("product_id"),s=n.parents(".inside");s.block({message:null,overlayCSS:{background:"#fff no-repeat center",opacity:.5,cursor:"none"}}),t.ajax({url:yith_wcwtl_meta.ajaxurl,dataType:"json",data:{action:"yith_waitlist_send_mail",product:a},success:function(t){t.send?s.html(t.msg):n.parents(".inside").find(".response-message").html(t.msg),s.unblock()}})})});